query:
query tableCustomerMachines($options: CustomerMachineOptions, $avatar: Boolean!, $serialNumber: Boolean!, $externalId: Boolean!, $machineModelName: Boolean!, $technology: Boolean!, $brand: Boolean!, $customerAddressCountry: Boolean!, $edgeDeviceConnectionStatus: Boolean!, $serviceContracts: Boolean!) {
  customerMachinesPaginated(options: $options) {
    data {
      ...MoveEdgeDeviceMachineValidationFields
      id
      avatar @include(if: $avatar)
      externalId @include(if: $externalId)
      serialNumber @include(if: $serialNumber)
      status
      proposal {
        id
        proposalRejection {
          reason
          __typename
        }
        __typename
      }
      customer {
        id
        name
        linkedTenant {
          id
          __typename
        }
        status
        address @include(if: $customerAddressCountry) {
          country
          __typename
        }
        __typename
      }
      machineModel {
        id
        avatar @include(if: $avatar)
        brand @include(if: $brand)
        externalId
        modelName @include(if: $machineModelName)
        technology @include(if: $technology)
        __typename
      }
      machine {
        id
        serialNumber
        brand @include(if: $brand)
        technology @include(if: $technology)
        owner {
          tenant {
            id
            name
            __typename
          }
          ownerType
          __typename
        }
        __typename
      }
      edgeDevice {
        id
        iotDeviceId
        status @include(if: $edgeDeviceConnectionStatus) {
          connectionState
          __typename
        }
        __typename
      }
      serviceContractAssignments {
        id
        startDate
        endDate
        serviceContract @include(if: $serviceContracts) {
          id
          contractName
          __typename
        }
        __typename
      }
      __typename
    }
    pageInfo {
      currentPage
      totalPages
      totalElements
      __typename
    }
    __typename
  }
}

fragment MoveEdgeDeviceMachineValidationFields on CustomerMachine {
  id
  status
  edgeDevice {
    id
    connectedAsset {
      id
      tenant {
        id
        __typename
      }
      __typename
    }
    connectedMachine {
      id
      tenant {
        id
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}
