query getCustomerMachine($id: ID!) {
  customerMachine(customerMachineId: $id) {
    id
    serialNumber
    displayName
    externalId
    customer {
      id
      name
      status
      linkedTenant {
        id
        __typename
      }
      __typename
    }
    machine {
      id
      edgeDevice {
        id
        iotDeviceId
        iotDeviceId
        status {
          connectionState
          __typename
        }
        product
        connected
        __typename
      }
      technology
      serialNumber
      brand
      modelName
      collaborationStatus
      owner {
        ownerType
        tenant {
          id
          name
          __typename
        }
        __typename
      }
      __typename
    }
    warrantyBegin
    warrantyEnd
    isSp1Migration
    edgeDeviceSerialNumber
    edgeDevice {
      id
      iotDeviceId
      status {
        connectionState
        __typename
      }
      __typename
    }
    avatar
    status
    machineModel {
      id
      avatar
      brand
      modelName
      technology
      isPublic
      __typename
    }
    serviceContractAssignments {
      id
      startDate
      endDate
      serviceContract {
        id
        contractName
        followTheSun
        isAssignable
        __typename
      }
      __typename
    }
    __typename
  }
}
