parameters.query:
query getAsset($assetId: ID!) {
  asset(assetId: $assetId) {
    id
    displayName
    avatar
    country
    isActive
    externalId
    facility {
      id
      __typename
    }
    serviceProviderTenant {
      id
      name
      __typename
    }
    machine {
      id
      serialNumber
      technology
      brand
      modelName
      owner {
        tenant {
          id
          name
          __typename
        }
        ownerType
        __typename
      }
      edgeDevice {
        id
        iotDeviceId
        status {
          connectionState
          __typename
        }
        product
        connected
        __typename
      }
      collaborationStatus
      __typename
    }
    tags {
      key
      value {
        id
        text
        __typename
      }
      __typename
    }
    serviceContractAssignments {
      id
      startDate
      endDate
      createdBy {
        id
        name
        __typename
      }
      createdAt
      updatedBy {
        id
        name
        __typename
      }
      updatedAt
      serviceContract {
        id
        contractName
        shortDescription
        escalateAfterMinutes
        isAssignable
        followTheSun
        __typename
      }
      __typename
    }
    machineDocuments {
      id
      category
      documentType
      documentUrl
      label
      language
      createdAt
      createdBy {
        id
        name
        __typename
      }
      owner {
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
}
