parameters.query:
query assetsPaginated($options: AssetOptions) {
  assetsPaginated(options: $options) {
    data {
      id
      displayName
      avatar
      country
      isActive
      externalId
      facility {
        id
        __typename
      }
      serviceProviderTenant {
        id
        name
        __typename
      }
      machine {
        id
        serialNumber
        technology
        brand
        modelName
        owner {
          tenant {
            id
            name
            __typename
          }
          ownerType
          __typename
        }
        edgeDevice {
          id
          iotDeviceId
          status {
            connectionState
            __typename
          }
          __typename
        }
        __typename
      }
      tags {
        key
        value {
          id
          text
          __typename
        }
        __typename
      }
      serviceContractAssignments {
        id
        startDate
        endDate
        serviceContract {
          id
          contractName
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
