query tableMachineModelMachineDocuments($machineModelId: ID!) {
  machineModel(machineModelId: $machineModelId) {
    id
    modelName
    brand
    technology
    machineDocuments {
      id
      category
      createdAt
      createdBy {
        id
        name
        __typename
      }
      fileDomainType
      documentType
      documentUrl
      label
      language
      owner {
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
}
