parameters.query:
query getMachineModel($id: ID!) {
  machineModel(machineModelId: $id) {
    id
    technology
    modelName
    brand
    avatar
    isPublic
    externalId
    tenant {
      id
      name
      __typename
    }
    assignedApplicationVersions {
      id
      version
      status
      application {
        id
        name
        status
        __typename
      }
      __typename
    }
    tunnelConfigTemplates {
      id
      displayId
      remoteIp
      remotePort
      tunnelType
      localIp
      localPort
      __typename
    }
    __typename
  }
}
