query machineModels($tenantLinkType: TenantLinkType) {
  machineModels(tenantLinkType: $tenantLinkType) {
    id
    technology
    modelName
    brand
    avatar
    isPublic
    externalId
    tenant {
      id
      name
      __typename
    }
    assignedApplicationVersions {
      id
      version
      status
      application {
        id
        name
        status
        __typename
      }
      __typename
    }
    __typename
  }
}
