parameters.query:
query getServiceContract($serviceContractId: ID!) {
  serviceContract(serviceContractId: $serviceContractId) {
    id
    contractName
    shortDescription
    escalateAfterMinutes
    isAssignable
    followTheSun
    serviceContractAssignments {
      id
      startDate
      endDate
      createdBy {
        id
        name
        __typename
      }
      createdAt
      updatedBy {
        id
        name
        __typename
      }
      updatedAt
      customerMachine {
        id
        serialNumber
        displayName
        status
        customer {
          id
          name
          status
          __typename
        }
        machineModel {
          id
          modelName
          brand
          technology
          __typename
        }
        machine {
          id
          serialNumber
          owner {
            tenant {
              id
              name
              __typename
            }
            ownerType
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
