parameters.query:
query serviceContractsPaginated {
  serviceContractsPaginated {
    data {
      id
      contractName
      shortDescription
      escalateAfterMinutes
      isAssignable
      followTheSun
      serviceContractAssignments {
        id
        startDate
        endDate
        createdBy {
          id
          name
          __typename
        }
        createdAt
        updatedBy {
          id
          name
          __typename
        }
        updatedAt
        customerMachine {
          id
          serialNumber
          customer {
            id
            name
            __typename
          }
          machineModel {
            id
            modelName
            brand
            technology
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
