query:
query serviceProviderServiceCaseByDisplayId($displayId: String!) {
  serviceCaseByDisplayId(displayId: $displayId) {
    ...ServiceProviderServiceCaseDetail
    __typename
  }
}

fragment ServiceProviderServiceCaseDetail on ServiceCase {
  id
  displayId
  isShared
  ...ServiceProviderServiceCaseTenant
  ...ServiceCaseHeader
  ...ServiceProviderServiceCaseOverviewSection
  ...ServiceCaseClosingReportSection
  ...ServiceCaseInfoSection
  ...ServiceCaseCustomerMachineSection
  ...ServiceCaseCustomFieldsSection
  ...ServiceCaseServiceContractSection
  ...ServiceProviderRemoteAccessSection
  ...ServiceProviderFileTransferSection
  __typename
}

fragment ServiceProviderServiceCaseTenant on ServiceCase {
  id
  operatorTenant {
    id
    __typename
  }
  serviceProviderTenant {
    id
    __typename
  }
  serviceCaseCustomerMachineSnapshot {
    id
    tenant {
      id
      __typename
    }
    __typename
  }
  __typename
}

fragment ServiceCaseHeader on ServiceCase {
  id
  displayId
  title
  isShared
  __typename
}

fragment ServiceProviderServiceCaseOverviewSection on ServiceCase {
  id
  title
  createdAt
  updatedAt
  closedAt
  isWatchedByCurrentUser
  externalId
  ...ServiceCaseState
  ...ServiceCaseAssignee
  ...ServiceCasePriority
  ...WatchServiceCase
  __typename
}

fragment ServiceCaseState on ServiceCase {
  id
  isShared
  state {
    id
    name
    index
    isClosed
    __typename
  }
  __typename
}

fragment ServiceCaseAssignee on ServiceCase {
  id
  isShared
  scmAssignee {
    id
    name
    __typename
  }
  state {
    id
    isClosed
    __typename
  }
  __typename
}

fragment ServiceCasePriority on ServiceCase {
  id
  priority
  state {
    id
    isClosed
    __typename
  }
  __typename
}

fragment WatchServiceCase on ServiceCase {
  id
  displayId
  isWatchedByCurrentUser
  __typename
}

fragment ServiceCaseClosingReportSection on ServiceCase {
  id
  state {
    id
    isClosed
    __typename
  }
  closingReport {
    id
    isSolved
    notes
    __typename
  }
  __typename
}

fragment ServiceCaseInfoSection on ServiceCase {
  id
  title
  description
  severity
  customFields {
    definition {
      key
      __typename
    }
    value
    __typename
  }
  scmCreator {
    id
    name
    __typename
  }
  serviceCaseCustomerMachineSnapshot {
    id
    customer {
      id
      name
      __typename
    }
    __typename
  }
  creatorContactEmail
  creatorPhoneNumber
  __typename
}

fragment ServiceCaseCustomerMachineSection on ServiceCase {
  id
  serviceCaseMachineSnapshot {
    id
    serialNumber
    modelName
    technology
    brand
    edgeDevice {
      id
      iotDeviceId
      __typename
    }
    __typename
  }
  serviceCaseCustomerMachineSnapshot {
    id
    machineModelId
    customer {
      id
      name
      __typename
    }
    __typename
  }
  serviceCaseServiceOrganisationSnapshot {
    id
    name
    serviceOrganisationId
    __typename
  }
  customerMachine {
    id
    avatar
    __typename
  }
  __typename
}

fragment ServiceCaseCustomFieldsSection on ServiceCase {
  id
  customFields {
    definition {
      id
      key
      displayName
      type
      selectableValues
      __typename
    }
    value
    __typename
  }
  __typename
}

fragment ServiceCaseServiceContractSection on ServiceCase {
  id
  scmServiceContractData {
    id
    contractName
    startDate
    endDate
    isValid
    __typename
  }
  __typename
}

fragment ServiceProviderRemoteAccessSection on ServiceCase {
  id
  isShared
  state {
    id
    isClosed
    __typename
  }
  serviceCaseMachineSnapshot {
    id
    edgeDevice {
      id
      iotDeviceId
      __typename
    }
    __typename
  }
  serviceCaseCustomerMachineSnapshot {
    id
    __typename
  }
  remoteAccess {
    id
    state
    active
    sessions {
      id
      state
      startDate
      endDate
      connection {
        deviceToMachineState
        deviceToTepsiState
        localToServiceState
        localToTepsiState
        __typename
      }
      configuration {
        localIp
        localPort
        localProxyId
        remoteIp
        remotePort
        __typename
      }
      deviceInfo {
        info
        __typename
      }
      technician {
        id
        name
        email
        __typename
      }
      webTunnelUrl
      __typename
    }
    __typename
  }
  __typename
}

fragment ServiceProviderFileTransferSection on ServiceCase {
  id
  isShared
  serviceCaseMachineSnapshot {
    id
    edgeDevice {
      id
      iotDeviceId
      __typename
    }
    __typename
  }
  __typename
}

query getMachineModel($id: ID!) {
  machineModel(machineModelId: $id) {
    ...DefaultMachineModelFragment
    __typename
  }
}

fragment DefaultMachineModelFragment on MachineModel {
  id
  technology
  modelName
  brand
  avatar
  isPublic
  externalId
  tenant {
    id
    name
    __typename
  }
  assignedApplicationVersions {
    id
    version
    status
    application {
      id
      name
      status
      __typename
    }
    __typename
  }
  tunnelConfigTemplates {
    id
    displayId
    remoteIp
    remotePort
    tunnelType
    localIp
    localPort
    __typename
  }
  __typename
}

query tableMachineModelMachineDocuments($machineModelId: ID!) {
  machineModel(machineModelId: $machineModelId) {
    id
    machineDocuments {
      id
      category
      createdAt
      createdBy {
        id
        name
        __typename
      }
      fileDomainType
      documentType
      documentUrl
      label
      language
      owner {
        id
        name
        __typename
      }
      __typename
    }
    __typename
  }
}
