parameters.query:
query getServiceCaseByDisplayId($displayId: String!) {
  serviceCaseByDisplayId(displayId: $displayId) {
    id
    displayId
    title
    description
    createdAt
    updatedAt
    closedAt
    firstTimeAssignedAt
    firstTimeInProgressAt
    isShared
    externalId
    priority
    severity

    state {
      id
      name
      index
      isClosed
    }

    creator {
      id
      name
      email
    }

    scmCreator {
      id
      name
    }

    assignee {
      id
      name
      email
    }

    scmAssignee {
      id
      name
    }

    closingReport {
      id
      isSolved
      notes
    }

    serviceCaseMachineSnapshot {
      id
      serialNumber
      modelName
      technology
      brand
      edgeDevice {
        id
        iotDeviceId
      }
    }

    serviceCaseCustomerMachineSnapshot {
      id
      machineModelId
      customer {
        id
        name
      }
      tenant {
        id
      }
    }

    serviceCaseServiceOrganisationSnapshot {
      id
      serviceOrganisationId
      name
    }

    operatorTenant {
      id
    }

    serviceProviderTenant {
      id
    }

    auditLog {
      changedByUser {
        id
        name
      }
      changedAt
      fieldName
      oldValue
      newValue
    }

    customFields {
      definition {
        id
        key
        displayName
        type
      }
      value
    }
  }
}
