parameters.query:
query getServiceCasesPaginated($view: ServiceCaseView!, $filter: ServiceCaseFilter) {
  serviceCasesPaginated(options: {
    view: $view
    filter: $filter
  }) {
    data {
      id
      displayId
      title
      description
      createdAt
      updatedAt
      closedAt
      priority
      severity
      externalId

      state {
        id
        name
        index
        isClosed
      }

      scmAssignee {
        id
        name
      }

      scmCreator {
        id
        name
      }

      serviceCaseMachineSnapshot {
        id
        modelName
        technology
        brand
        serialNumber
      }

      serviceCaseCustomerMachineSnapshot {
        id
        customer {
          id
          name
        }
      }

      serviceCaseServiceOrganisationSnapshot {
        id
        name
      }

      closingReport {
        id
        isSolved
      }
    }
  }
}
