parameters.query:
mutation createCustomer($name: String!, $serviceOrganisationId: ID!, $country: String!, $externalId: String, $websiteUrl: String, $city: String, $postalCode: String, $street: String, $street2: String, $state: String) {
  createCustomer(customer: {
    name: $name
    serviceOrganisation: $serviceOrganisationId
    externalId: $externalId
    websiteUrl: $websiteUrl
    address: {
      country: $country
      city: $city
      postalCode: $postalCode
      street: $street
      street2: $street2
      state: $state
    }
  }) {
    id
    name
    status
    externalId
    websiteUrl
    serviceOrganisation {
      id
      name
    }
    address {
      country
      city
      postalCode
      street
      street2
      state
    }
  }
}
