parameters.query:
query getAllCustomers {
  customersPaginated(options: {}) {
    data {
      id
      tenantId
      name
      status
      linkedTenant {
        id
        name
      }
      externalId
      websiteUrl
      serviceOrganisation {
        id
        name
      }
      address {
        country
        state
        city
        postalCode
        street
        street2
      }
    }
  }
}
