parameters.query:
query getAllCustomers($tenantId: ID!, $limit: Int, $offset: Int) {
  customers(filter: { tenantId: $tenantId }, limit: $limit, offset: $offset) {
    id
    name
    email
    status
    createdAt
    updatedAt
    tenant {
      id
      name
    }
    contactInfo {
      phone
      address
    }
  }
  customersCount(filter: { tenantId: $tenantId })
}
