parameters.query:
query getCustomersByStatus($status: CustomerStatus!, $page: Int!, $pageSize: Int!) {
  customersPaginated(options: {
    filter: {
      status: { is: $status }
    }
    pagination: { page: $page, pageSize: $pageSize }
  }) {
    data {
      id
      tenantId
      name
      status
      linkedTenant {
        id
        name
      }
      externalId
      websiteUrl
      address {
        country
        state
        city
        postalCode
        street
        street2
      }
    }
    pageInfo {
      totalElements
      totalPages
      currentPage
    }
  }
}
