parameters.query:
query getCustomersByStatus($status: CustomerStatus!) {
  customersPaginated(options: {
    filter: {
      status: { is: $status }
    }
  }) {
    data {
      id
      tenantId
      name
      status
      linkedTenant {
        id
        name
      }
      externalId
      websiteUrl
      serviceOrganisation {
        id
        name
      }
      address {
        country
        state
        city
        postalCode
        street
        street2
      }
    }
  }
}
