query getCustomersByStatus($status: String!, $tenantId: ID!) {
  customers(filter: { status: $status, tenantId: $tenantId }) {
    id
    name
    email
    status
    createdAt
    updatedAt
    tenant {
      id
      name
    }
    contactInfo {
      phone
      address
    }
  }
}
