parameters.query:
query getUserCount {
  allUsers: usersPaginated(options: { pagination: { page: 1, pageSize: 1 } }) {
    pageInfo {
      totalElements
    }
  }
  activeUsers: usersPaginated(options: {
    filter: { isActive: true }
    pagination: { page: 1, pageSize: 1 }
  }) {
    pageInfo {
      totalElements
    }
  }
  inactiveUsers: usersPaginated(options: {
    filter: { isActive: false }
    pagination: { page: 1, pageSize: 1 }
  }) {
    pageInfo {
      totalElements
    }
  }
}
