query getUserCount($tenantId: ID!) {
  totalUsers: usersCount(filter: { tenantId: $tenantId })
  activeUsers: usersCount(filter: { tenantId: $tenantId, isActive: true })
  inactiveUsers: usersCount(filter: { tenantId: $tenantId, isActive: false })
  technicalUsers: usersCount(filter: { tenantId: $tenantId, isTechnicalUser: true })
}
