query getUsersByStatus($isActive: Boolean!) {
  usersPaginated(options: {
    filter: { isActive: $isActive }
  }) {
    data {
      id
      identityId
      email
      name
      status
      isActive
      isTechnicalUser
      tenant {
        id
        name
      }
      settings {
        portal
      }
    }
  }
}
