parameters.query:
query getUsersByStatus($isActive: Boolean!, $page: Int!, $pageSize: Int!) {
  usersPaginated(options: {
    filter: { isActive: $isActive }
    pagination: { page: $page, pageSize: $pageSize }
  }) {
    data {
      id
      identityId
      email
      name
      status
      isActive
      isTechnicalUser
      tenant {
        id
        name
      }
      settings {
        portal
      }
    }
    pageInfo {
      totalElements
      totalPages
      currentPage
    }
  }
}
