parameters.query:
mutation inviteCustomer($customerId: ID!, $userName: String!, $userEmail: String!) {
  inviteCustomer(
    customerId: $customerId
    userName: $userName
    userEmail: $userEmail
  ) {
    id
    name
    status
    linkedTenant {
      id
      name
    }
    serviceOrganisation {
      id
      name
    }
  }
}
